mapper.update:
UPDATE "BOARD" SET
BOARD_TITLE = #{boardTitle},
BOARD_TEXT  = #{boardText},
TAG_NO = #{tagNo},
BOARD_THUMBNAIL = #{thumbnail},
UPDATE_DATE = SYSDATE 
WHERE BOARD_CODE = #{boardCode}
AND COUNTRY_NO = #{countryNo}
AND BOARD_NO = #{boardNo}

UPDATE "BOARD" SET
BOARD_DELETE = 'Y'
WHERE BOARD_NO = #{boardNo}
AND BOARD_CODE = #{boardCode}

UPDATE "BOARD" SET
BOARD_DELETE = 'Y'
WHERE BOARD_NO = #{boardNo}
AND COUNTRY_NO = #{countryNo}
AND BOARD_CODE = #{boardCode}
